import * as React from "react";
import {useState} from "react";
import {CardType} from "../../type/CardType";

export const Description: React.FC<{ card: CardType, editable: boolean, handleSave: (card: CardType) => void }>
  = ({card, editable, handleSave}) => {
  const {
    description
  } = card;
  const [descriptionInput, setDescriptionInput] = useState(description);
  const [editing, setEditing] = useState(false);
  const handleEditMode = () => {
    if (!editable) {
      return;
    }
    setEditing(!editing);
    setDescriptionInput(description);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setDescriptionInput(e.target.value)
  };

  const handleClick = () => {
    const updatedCard: CardType = {
      ...card,
      description: descriptionInput
    };
    handleSave(updatedCard);
    setEditing(false);
  };

  if (editing || !description) {
    return (
      <div className="card-detail-wrapper">
        <div className="card-detail-content">Description</div>
        <input
          className="card-detail-input"
          type="text"
          value={descriptionInput}
          onChange={handleChange}
          placeholder="Add a more detailed descriptionâ€¦"
        />
        <img
          className="description-btn"
          src="data:image/png;base64,"
          alt="save"
          onClick={handleClick}
        />
        <img
          className="description-btn"
          src="data:image/png;base64,"
          alt="cancel"
          onClick={handleEditMode}
        />
      </div>
    )
  }

  return (
    <h3 className="card-detail-description">
      Description<br/>
      <div
        className="description-text"
        onClick={handleEditMode}
        style={editable ? {cursor: "pointer"} : {}}
      >
        {description}
      </div>
    </h3>
  )
};